import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';

const JoinParty = () => {

    const [entryCode, setEntryCode] = useState({
        value: ''
    });

    const handleChange = e => {
        const regex = /^[0-9\b]+$/, val = e.target.val;

        if (val === '' || regex.test(valvue)) 
            setEntryCode({value: e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault();
        console.log(entryCode.value)
    }

    return(
        <form onSubmit={handleSubmit} className='hnp-wrapper content section'>
            <h4 className='title'>Please enter your party's entry code.</h4>

            <input type='text' value={entryCode.value} onChange={handleChange} />

            <Button variant='primary' type='submit'>Submit</Button>
        </form>

    )
}

export default JoinParty;
