import React from 'react';
import './styles.css';



const Result = ({ song, addSong }) => {
    const isMobile = useWindowSize().width < 768, currentSongIdx = getCurrentSongIdx(queue);

    <div className='result bordered has-background-white' 
            onClick={() => addSong(song)}>

        <img src={song.album.images[0].url} width={isMobile ? "90%" : "200px"} alt="song" />
        <h4>{song.name} by {song.artists[0].name}</h4>

    </div>
}

export default Result;
