import React, { useState } from 'react';
import Result from './Result';
import "./styles.css";

const AddSong = ({ addSong, results, getResults}) => {

    const [query, setQuery] = useState({
        value: ''
    });

    const handleChange = e => {
        e.preventDefault();
        setQuery({ value: e.target.value });
        getResults(query.value);
    }

    return(
        <div className='add-song'>
            <input type='text' value={query.value} onChange={handleChange} />

            <div className="search-input-wrapper">

                        <img 
                            src="/search.png" width="25px" height="25px" alt="search" 
                            style={{marginRight: "-40px", zIndex: "999"}}
                        />

                        <input 
                            type="text"
                            name="query" 
                            className="search-input"
                            placeholder="Add a song to the queue..."
                            onChange={this.getResults} 
                        />
                    </div>

            {results && results.map(result => <Result addSong={addSong} key={result.id} song={result} />)}
        </div>
    )
}

export default AddSong;
