import React, { useState } from 'react';

const [entryCode, setEntryCode] = useState('');

const HostParty = () => {

    const handleChange = e => {
        e.preventDefault();
        setEntryCode(e.target.value);
    }

    return(
        <div className='hnp-wrapper content section'>
            <h4 className='title'>Please enter your party's entry code.</h4>

            <input type='text' value={entryCode} onChange={handleChange} />
        </div>

    )
}

export default HostParty;
