import React from 'react';

const Members = ({ members, host }) => (
    <>
        <h3 className='subtitle has-text-light'>Members</h3>

        <ul>
            {members.map(member => {
                let output = member.nam;

                if(member.id === host.id) {
                    output += 'HOST';
                    classes += 'green';
                }

                return <li>{output} </li>
            })}
        </ul>
    </>   
)

export default Members;
