import React from 'react';

const Members = ({ members, host }) => (
    <>
        <h3 className='subtitle has-text-light'>Members</h3>

        <ul>
            {members.map(member => {
                let output = member.name;
                const isHost = member.id === host.id;

                if(isHost) 
                    output += 'HOST';


                return <li key={member.id} style={{color: isHost ? "#1DB954" : "auto"}}>{output} </li>
            })}
        </ul>
    </>   
)

export default Members;
