import React, { Component } from 'react';
import Mobile from './mobile';
import axios from 'axios';
import { apiHost } from 'config';

class PartyContainer extends Component {

    state = {currentSong: null};

    getCurrentSong = () => {
        const { party } = this.props;
        const { currentSong } = this.state;

        if(!party || !party.host) return;

        const token = party.host.token;
        axios.get("https://api.spotify.com/v1/me/player/currently-playing", {
            headers: {
                'Authorization': 'Bearer ' + token
            }
        }).then(response => {
            const song = response.data.item;
            song && (!currentSong || song.id !== currentSong.id) && this.changeCurrentSong(song);
        })
    }

    changeCurrentSong = song => {
        const { party } = this.props;
        this.setState({currentSong: song});

        axios.post(`${apiHost}/api/songs/current?partyId=${party.id}`, {
            artist: song.artists && song.artists[0] && song.artists[0].name,
            imageURI: song.album && song.album.images && song.album.images[0] && song.album.images[0].url,
            name: song && song.name
        });
    }

    componentDidMount() {
        const { party, member } = this.props;

        if(member.id !== party.host.id) return;
        setInterval(this.getCurrentSong, 2000);
    }

    render() {
        const { currentSong } = this.state;
        const { party, member } = this.props;
        return(
            <div>
                <Mobile party={{...party, currentSong}} member={member} />
            </div>
        )
    }
}

export default PartyContainer;
