import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import './App.css';
import 'bulma/css/bulma.css'

import Navbar from 'components/navbar';
import Landing from 'components/landing';
import Party from 'components/party/PartyContainer';

import { apiHost } from 'config';
import queryString from 'query-string';
import axios from 'axios';
import ProtectedRoute from 'hoc/ProtectedRoute';

function App() {

  const token = queryString.parse(window.location.search).access_token;
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));

  const fetchUser = () => {
    !user && token && axios.get('https://api.spotify.com/v1/me',
          { headers: { 'Authorization': 'Bearer ' + token} }
      ).then(response => storeUser(response.data))
  }

  const storeUser = user => {
    axios.post(`${apiHost}/api/members`, {
      name: user.display_name, email: user.email, token: token
    })
      .then(response => {
        const theUser = response.data;
        setUser(theUser);
        localStorage.setItem('user', JSON.stringify(theUser)) 
      })
  }

  useEffect(fetchUser, []);

  return (
    <div className="App">

      <Navbar user={user} />

      <div className='has-background-light' style={{minHeight: 'calc(100vh - 70px)'}}>
          <Router>
            <Switch>
              <Route exact={true} path='/' component={Landing} />
              <ProtectedRoute user={user} exact={true} path='/party' component={Party} />
            </Switch>
          </Router>
        </div>
    </div>
  );
}

export default App;
