import React from 'react';
import Button from 'react-bootstrap/Button';
import './HasNoParty.css';
import HostParty from './host'

const HasNoParty = ({ member }) => {

    const [choice, setChoice] = useState();

    hostParty = () => setChoice('host');
    joinParty = () => setChoice('join');

    if(!choice) 
        return(
            <div className='hnp-wrapper content section'>

                <h4 className='title'>What do you want to do?</h4>
                <div className='hnp-button-group'>
                    <Button onClick={hostParty} variant='primary'>Host Party</Button>
                    <Button onClick={joinParty} variant='primary'>Join Party</Button>
                </div>

            </div>
        )

    if(choice === 'host')
            return <HostParty />

    return <JoinParty />

}

export default HasNoParty;
