import React from 'react';

const hostTag = <strong style={{color: "#1DB954"}}></strong>

const Members = ({ members, host }) => (
    <>
        <h3 className='subtitle has-text-light'>Members</h3>

        <ul>
            {members.map(member => {
                const isHost = member.id === host.id;

                if(isHost) 
                    output += 'HOST';


                return <li key={member.id}>{member.name} {isHost && hostTag} </li>
            })}
        </ul>
    </>   
)

export default Members;
