import React, { useState } from 'react';
import BottomNav from './BottomNav';
import Queue from './tabs/Queue';
import AddSong from './tabs/AddSong';
import Members from './tabs/Members';

const MobileParty = ({ member, party}) => {

    const [visibleTab, setVisibleTab] = useState("Queue");

    return(
        <div>
            {visibleTab == "Queue" && <Queue queue={party.queue} /> }
            {visibleTab == "Add Song" && <AddSong member={member} />}
            {visibleTab == "Members" && <Members members={party.members} />}

            <BottomNav setVisible={setVisibleTab} />
        </div>

    )
}

export default MobileParty;
