import React, { useState, useEffect } from 'react';
import Mobile from './mobile';
import axios from 'axios';
import { apiHost } from 'config';

const PartyContainer = ({ member, party }) => {

    const currentSong = JSON.parse(localStorage.getItem('song'));

    const getCurrentSong = () => {
        if(!party || !party.host) return;

        const token = party.host.token;
        axios.get("https://api.spotify.com/v1/me/player/currently-playing", {
            headers: {
                'Authorization': 'Bearer ' + token
            }
        }).then(response => {
            const song = response.data.item;

            if(!song) return;

            if(currentSong && song) console.log(currentSong.id === song.id) 

            song && (!currentSong || song.id !== currentSong.id) && changeCurrentSong(song);
        })
    }

    const changeCurrentSong = song => {
        localStorage.setItem('song', JSON.stringify(song));

        axios.post(`${apiHost}/api/songs/current?partyId=${party.id}`, {
            artist: song.artists && song.artists[0] && song.artists[0].name,
            imageURI: song.album && song.album.images && song.album.images[0] && song.album.images[0].url.
            song && song.name
        });
    }

    useEffect(() => {
        if(member.id !== party.host.id) return;
        const interval = setInterval(getCurrentSong, 2000);
        return () => clearInterval(interval);
    }, []);


    return(
        <div>
            <Mobile party={{...party, currentSong}} />
        </div>
    )
}

export default PartyContainer;
